function filterSelection(e) {
  let t, l;
  for (t = document.getElementsByClassName("filter-web"), "all" === e && (e = ""), l = 0; l < t.length; l++) RemoveClass(t[l], "show"), t[l].className.indexOf(e) > -1 && AddClass(t[l], "show");
}
function AddClass(e, t) {
  let l, s, n;
  for (s = e.className.split(" "), n = t.split(" "), l = 0; l < n.length; l++) -1 === s.indexOf(n[l]) && (e.className += " " + n[l]);
}
function RemoveClass(e, t) {
  let l, s, n;
  for (s = e.className.split(" "), n = t.split(" "), l = 0; l < n.length; l++) for (; s.indexOf(n[l]) > -1; ) s.splice(s.indexOf(n[l]), 1);
  e.className = s.join(" ");
}
filterSelection("all");
